import { Component, Input, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Doctor } from '../models/doctor.model';
import { OverlayToggleService } from '../services/overlay-toggle/overlay-toggle.service';

@Component({
  selector: 'app-details-overlay',
  templateUrl: './details-overlay.component.html',
  styleUrls: ['./details-overlay.component.scss']
})
export class DetailsOverlayComponent implements OnInit{
  showOverlay: boolean = false;
  @Input() currentDoctor : Doctor;
  detailsForm : FormGroup;

  constructor(private toggleService : OverlayToggleService, private fb : FormBuilder){}


  ngOnInit(){
    this.detailsForm = this.fb.group({
      name : '',
      surname : '',
      email : '',
      address : ''
    });
    this.detailsForm.valueChanges.subscribe();
    this.toggleService.clickedEvent.subscribe((value:boolean)=>{
      this.showOverlay = value;
    });
  }

  closeOverlay(e:any){
    if(e == 'force-close'){
      this.showOverlay = false;
      return
    }

    if(this.showOverlay){
      if(e.srcElement.className == 'overlay'){
        this.showOverlay = false;
      }
    }
  }

  updateDoctorInfo(){
    const values = this.detailsForm.value;
    let updatedDoctor = new Doctor(this.currentDoctor);
    updatedDoctor = this.currentDoctor;
    for(const value in values){
      if(values[value]){
        if(value == 'name'){
          // console.log(values[value]);
          updatedDoctor.name = values[value];
        }
        else if(value == 'surname'){
          console.log(values[value]);
        }
        else if(value == 'email'){
          console.log(values[value]);
        }
        else if(value == 'address'){
          console.log(values[value]);
        }
      }
    }

    console.log(updatedDoctor);

  }

  deleteDoctor(){
    console.log(this.currentDoctor);
  }
}
<div class="overlay" *ngIf="showOverlay" (click)="closeOverlay($event)">
    <div class="info-panel">
        <form [formGroup]="detailsForm" (ngSubmit)="updateDoctorInfo()">
            <div class="txt-field">
                <label>Name:</label>
                <input formControlName="name" type="text">
            </div>
            <div class="txt-field">
                <label>Surname:</label>
                <input formControlName="surname" type="text">
            </div>
            <div class="txt-field">
                <label>Email:</label>
                <input formControlName="email" type="text">
            </div>
            <div class="txt-field">
                <label>Address:</label>
                <input formControlName="address" type="text">
            </div>
            <div class="form-row">
                <div class="txt-field">
                    <input type="submit" value="Save Changes">
                </div>
                <button type="button" (click)="deleteDoctor()">Delete Doctor</button>
                <button type="button" (click)="closeOverlay('force-close')">Close</button>
            </div>

        </form>
    </div>
    <!-- <button (click)="closeOverlay()">Close</button> -->
</div>
