import { Component, Input, OnInit } from '@angular/core';
import { Form, FormBuilder, FormGroup } from '@angular/forms';
import { Doctor } from '../models/doctor.model';

@Component({
  selector: 'new-instance',
  templateUrl: './new-instance.component.html',
  styleUrls: ['./new-instance.component.scss']
})
export class NewInstanceComponent implements OnInit {
  @Input() buttonInfo : string;
  @Input() doctor : Doctor;
  @Input() service : Doctor;
  screenVisible : boolean = false;
  @Input()insertForm : boolean = false;
  newDoctorForm : FormGroup;

  constructor (private fg: FormBuilder){}

  ngOnInit(){
    this.newDoctorForm = this.fg.group({
      name:'',
      surname:'',
      age:'',
      emailAddress: '',
      phoneNumber:'',
      homeAddress:'',
      speciality:'',
      gender:''
    });
    this.newDoctorForm.valueChanges.subscribe();
  }

  toggleScreen(e: any){
    if(e == 'force-toggle'){
      this.screenVisible = !this.screenVisible;
      return
    }

    if(this.screenVisible){
      if(e.srcElement.className == 'overlay'){
        this.screenVisible = !this.screenVisible;
      }
    }
    else{
      this.screenVisible = !this.screenVisible;
    }
  }

  createDoctor(){

    console.log(this.newDoctorForm);
    this.screenVisible = false;
  }
}
<button class="toggle-btn open-btn" (click)="toggleScreen($event)">{{buttonInfo}}</button>


<div *ngIf="screenVisible" (click)="toggleScreen($event)" class="overlay">
    <div class="info-screen details-screen" *ngIf="doctor != null && !insertForm">
        <div class="info-section">
            <h1 class="title">{{doctor.name + ' ' + doctor.surname}}</h1>
        </div>
        <div class="info-section">
            <ul class="info-list">
                <li> <span class="label-txt">Speciality:</span> {{doctor.speciality}}</li>
                <li> <span class="label-txt">Age:</span> {{doctor.age}}</li>
                <li> <span class="label-txt">Gender:</span> {{doctor.gender}}</li>
                <li> <span class="label-txt">Phone Number:</span> <span class="contact">{{doctor.phoneNumber}}</span></li>
                <li> <span class="label-txt">Email:</span> <span class="contact">{{doctor.emailAddress}}</span></li>
            </ul>
        </div>
        <div class="info-section">
            <button class="toggle-btn close-btn" (click)="toggleScreen('force-toggle')">Close</button>
        </div>
    </div>
    <!-- Needs to be changed according to service DTO -->
    <div class="info-screen" *ngIf="service != null && !insertForm">
        <div class="info-section">
            <h1 class="title">{{service.name + ' ' + service.surname}}</h1>
        </div>
        <div class="info-section">
            <ul class="info-list">
                <li> <span class="label-txt">Speciality:</span> {{service.speciality}}</li>
                <li> <span class="label-txt">Age:</span> {{service.age}}</li>
                <li> <span class="label-txt">Gender:</span> {{service.gender}}</li>
                <li> <span class="label-txt">Phone:</span> <span class="contact">{{service.phoneNumber}}</span></li>
                <li> <span class="label-txt">Email:</span> <span class="contact">{{service.emailAddress}}</span></li>
            </ul>
        </div>
        <div class="info-section">
            <button class="toggle-btn close-btn" (click)="toggleScreen('force-toggle')">Close</button>
        </div>
    </div>

    <div class="info-screen form-screen" *ngIf="doctor != null && insertForm">
        <div class="info-section">
            <h1 class="title">Add a new doctor</h1>
        </div>
        <div class="form-section">
            <form class="form-screen" [formGroup]="newDoctorForm" (ngSubmit)="createDoctor()">
            
                <div class="txt-field">
                    <label class="field-label">Name:</label>
                    <input formControlName="name" type="text">
                </div>
                <div class="txt-field">
                    <label class="field-label">Surname:</label>
                    <input formControlName="surname" type="text">
                </div>
                <div class="txt-field">
                    <label class="field-label">Age:</label>
                    <input formControlName="age" type="text">
                </div>
